import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import socket from "../socket";
import styles from "../styles/Chat.module.css";

function ChatRoom() {
    const navigate = useNavigate();
    const [users, setUsers] = useState([]);
    const [messages, setMessages] = useState({});
    const [selectedUser, setSelectedUser] = useState(null);
    const [input, setInput] = useState("");

    const currentUser = localStorage.getItem("username");

    useEffect(() => {
        if (!currentUser) {
            navigate("/login"); // Redirect to login if no user is found
        } else {
            socket.emit("register", currentUser);
        }

        socket.on("userList", (userList) => {
            setUsers(userList.filter(user => user !== currentUser)); // Show only online users, excluding self
        });

        socket.on("message", ({ sender, message }) => {
            setMessages((prev) => ({
                ...prev,
                [sender]: [...(prev[sender] || []), { sender, message }]
            }));
        });

        return () => {
            socket.off("userList");
            socket.off("message");
        };
    }, [currentUser]);

    const sendMessage = () => {
        if (input.trim() && selectedUser) {
            socket.emit("message", { sender: currentUser, recipient: selectedUser, message: input });

            setMessages((prev) => ({
                ...prev,
                [selectedUser]: [...(prev[selectedUser] || []), { sender: currentUser, message: input }]
            }));

            setInput("");
        }
    };

    return (
        <div className={styles.chatContainer}>
            <div className={styles.userList}>
                <h3>Online Users</h3>
                {users.length > 0 ? (
                    users.map((user) => (
                        <div key={user} className={styles.userItem} onClick={() => setSelectedUser(user)}>
                            {user}
                        </div>
                    ))
                ) : (
                    <p>No users online</p>
                )}
            </div>

            <div className={styles.chatBox}>
                <h2 className={styles.welcomeMessage}>Welcome to the Chat Room, {currentUser}</h2>

                <div className={styles.messageArea}>
                    {(messages[selectedUser] || []).map((msg, index) => (
                        <p key={index} className={msg.sender === currentUser ? styles.sent : styles.received}>
                            {msg.message}
                        </p>
                    ))}
                </div>

                {selectedUser && (
                    <>
                        <input value={input} onChange={(e) => setInput(e.target.value)} placeholder="Type a message..." />
                        <button onClick={sendMessage}>Send</button>
                    </>
                )}
            </div>
        </div>
    );
}

export default ChatRoom;
