import { useState, useEffect } from "react";
import socket from "../socket";
import styles from "../styles/Chat.module.css";

function Chat({ currentUser }) {
    const [users, setUsers] = useState([]);
    const [messages, setMessages] = useState({});
    const [selectedUser, setSelectedUser] = useState(null);
    const [input, setInput] = useState("");

    useEffect(() => {
        socket.emit("register", currentUser);

        const handleUserList = (userList) => {
            console.log("Received user list:", userList);
            setUsers(userList); // Store only online users
        };

        const handleMessage = ({ sender, message }) => {
            console.log(`Received message from ${sender}: ${message}`);
            setMessages((prev) => ({
                ...prev,
                [sender]: [...(prev[sender] || []), { sender, message }]
            }));
        };

        socket.on("userList", handleUserList);
        socket.on("message", handleMessage);

        return () => {
            socket.off("userList", handleUserList);
            socket.off("message", handleMessage);
        };
    }, [currentUser]);

    const sendMessage = () => {
        if (input.trim() && selectedUser) {
            console.log("Sending message:", { sender: currentUser, recipient: selectedUser, message: input });
            socket.emit("message", { sender: currentUser, recipient: selectedUser, message: input });

            setMessages((prev) => ({
                ...prev,
                [selectedUser]: [...(prev[selectedUser] || []), { sender: currentUser, message: input }]
            }));

            setInput("");
        }
    };

    return (
        <div className={styles.chatContainer}>
            <div  className={styles.welcomeMessage}>
            <div className={styles.userList}>
                <h3>Online Users</h3>
                {users.length > 0 ? (
                    users.map((user) => (
                        <div key={user} className={styles.userItem} onClick={() => setSelectedUser(user)}>
                            {user}
                        </div>
                    ))
                ) : (
                    <p>No users online</p>
                )}
            </div>

            <div className={styles.chatBox}>
                <h3>Chat with {selectedUser || "..."}</h3>
                <div className={styles.messageArea}>
                    {(messages[selectedUser] || []).map((msg, index) => (
                        <p key={index} className={msg.sender === currentUser ? styles.sent : styles.received}>
                            {msg.message}
                        </p>
                    ))}
                </div>
                {selectedUser && (
                    <>
                        <input value={input} onChange={(e) => setInput(e.target.value)} placeholder="Type a message..." />
                        <button onClick={sendMessage}>Send</button>
                    </>
                )}
            </div>
        </div>
        </div>
    );
}

export default Chat;
